import React, { useEffect, useState } from 'react';
import Chessboard from 'chessboardjs';
import Chess from 'chessjs';

// There Is Probably An Easier Way To Do This, But I Did It The Same Way As The Chatbox, Both For Organization, And Because I Know
// This Method Already Works. Documentation On This Is Lacking but https://chessboardjs.com/examples#1000
const ChessGame = ({ socket, room }) =>
{
    const [game, setGame] = useState(new Chess());
    const [board, setBoard] = useState(null);

    useEffect(() =>
    {
        const board = Chessboard('game-board', 'start');
        setBoard(board);

        //socket.on('move', recieveMove);

        return (socket.off('move'));
    }, [])

    const sendMove = (source, target) =>
    {
        const move = game.move({from: source, to: target});

        if (move != null)
        {
            socket.emit('move', { source, target, room });
        }
            
        //updateBoard();

    }

    return (
        <h1></h1>
    )
}

export default ChessGame
