import Chessboard from "chessboardjs";


const createBoard = () =>
{
    // Get A Reference To The Gameplay Container
    let gameContainer = document.querySelector('#game-container');

    // Define And Add The Board To This Container
    let gameBoard = document.createElement('div');
    gameBoard.id = 'game-board';   
    gameContainer.appendChild(gameBoard);

    // Board Configs (Documentation Says To Use Var So Im Just Doing It)
    var config = 
    {
        draggable: true,
        position: 'start'
    }

    // Create The Board Object, Referencing The DOM Element That Now Exists For It (Documentation Says To Use Var So Im Just Doing It)
    var board = Chessboard(gameBoard.id, config);
}
