import React from 'react';

function Chat ({ socket, name, room })
{
    // useState To Keep Track Of The Message Currently Being Typed
    const [currentMsg, setCurrentMsg] = useState("");

    return(
        <div id="chatbox">
            <div id="chat-header">
                <h2>Live Chat</h2>
            </div>
            <div id="chat-body"></div>
            <div id="chat-footer">
                <input id="message-input" type="text" placeholder="Message your oppenent" />
                <button id="btn-send">Send</button>
            </div>
        </div>
    )
}

export default Chat;
