import React, { useEffect } from 'react';

function Chat ({ socket, name, room })
{
    // useState To Keep Track Of The Message Currently Being Typed
    const [currentMsg, setCurrentMsg] = React.useState("");

    // Function To Be Called When Send Button Is Pressed, Sends The Message Out To The Other Person In The Room
    // Not Sure Why It Needs Async, But Intellisense Was Being Rude
    const sendMessage = async () =>
    {
        // Only Send A Message If It Isn't Blank
        if(currentMsg !== "")
        {
            // All The Data About The Message That Needs To Be Sent To Socket
            const messageData = 
            {
                room: room,
                sender: name,
                message: currentMsg,
                timeSent: new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes(),
            }

            await socket.emit("sendMessage", messageData);
        }
    }

    React.useEffect(() =>
    {

    })

    return(
        <div id="chatbox">
            <div id="chat-header">
                <h2>Live Chat</h2>
            </div>
            <div id="chat-body"></div>
            <div id="chat-footer">
                <input id="message-input" type="text" placeholder="Message your oppenent" onChange={(event => {setCurrentMsg(event.target.value);})}/>
                <button id="btn-send" onClick={sendMessage}>Send</button>
            </div>
        </div>
    )
}

export default Chat;
